INSERT INTO skills VALUES(596,'待ち伏せ3',NULL,NULL,NULL,'Vantage 3','自分のHPが75%以下で、敵から攻撃された時、先制攻撃','|待ち伏せ2|','',NULL,'','','','','パッシブB','',NULL,'','可',NULL,NULL,NULL,NULL,NULL,NULL,NULL,200,'','',replace('    <p>待ち伏せに適正のあるキャラの特徴は、反撃時の一撃の火力が大きいキャラです。以下のようなキャラが該当します。\n    </p>\n    <ul>\n      <li>反撃時に2回攻撃可能なキャラ</li>\n      <ul>\n        <li>剣ラインハルト、フランネル、オルティナ、伝承リーフ、ラズワルド、ソワレ、ソール</li>\n      </ul>\n      <li>ブレード持ちのキャラ</li>\n      <ul>\n        <li>ニノ、サーリャ、緑魔オルエン、レーヴァテインなど</li>\n      </ul>\n      <li>奥義を反撃時に毎回放てるキャラ</li>\n      <ul>\n        <li>アレス</li>\n      </ul>\n      <li>武器にダメージ加算効果のあるキャラ</li>\n      <ul>\n        <li>剣カチュア、伝承アルムなど</li>\n      </ul>\n      <li>特効持ち</li>\n      <ul>\n        <li>伝承クロム、子供シーダ、弓(vs 飛行パ)など</li>\n      </ul>\n    </ul>\n    また、全距離に反撃できると敵を全滅させることができるので遠距離反撃や近距離反撃とセットで装備するケースが多いです。\n','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
