INSERT INTO skills VALUES(1234,'フェザーソード',NULL,'2024-02-06',NULL,'Feather Sword','剣、槍、斧、無属性弓、重装特効<br>攻撃+3<br>自分のHPが75%以下、または、敵が剣、槍、斧、無属性弓、重装なら、敵から攻撃された時、先制攻撃',NULL,'','true','剣、槍、斧、無属性弓、重装特効<br>攻撃+3<br>敵から攻撃された時、または、戦闘開始時、敵のHPが75%以上の時、戦闘中、攻撃、速さ、守備、魔防+4、敵の絶対追撃を無効、かつ、自分の追撃不可を無効<br>自分のHPが90%以下、または、敵が剣、槍、斧、無属性弓、重装なら、敵から攻撃された時、先制攻撃','','【再移動(2)】を発動可能<br>戦闘開始時、自身のHPが25%以上なら、戦闘中、攻撃、速さ、守備、魔防+4、さらに、攻撃、速さが周囲3マス以内の味方の人数×3だけ上昇(最大6)、最初に受けた攻撃と2回攻撃のダメージを40%軽減(最初に受けた攻撃と2回攻撃:通常の攻撃は、1回目の攻撃のみ。「2回攻撃」は、1～2回目の攻撃)','3','武器','',NULL,'剣','不可','','',NULL,16,NULL,NULL,NULL,400,'|剣|槍|斧|無属性弓|重装|',NULL,'',NULL,3,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
