INSERT INTO skills VALUES(3019,'連魔弾','2024-09-17',NULL,'','','奥義発動時、速さの50%を奥義ダメージに加算、かつ敵の守備か魔防の低い方でダメージ計算1～4ターン目、戦闘中、自分の最初の攻撃前に奥義発動カウント-2、自分の最初の追撃前に奥義発動カウント-2自分または敵が奥義発動可能状態の時、または、この戦闘(戦闘前、戦闘中)で自分または敵が奥義発動済みの時、戦闘中、受けた攻撃のダメージを40%軽減(1戦闘1回のみ)(範囲奥義を除く)','|華炎|','',NULL,'','','',NULL,'奥義','',NULL,'','不可','','',3,NULL,NULL,NULL,NULL,500,NULL,NULL,'',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
