INSERT INTO skills VALUES(3021,'称賛の希求の大斧','2024-09-18',NULL,'','','飛行特効<br>守備+3<br>敵の守備か魔防の低い方でダメージ計算<br>ターン開始時、自身のHPが25%以上なら、自分と周囲2マス以内の味方の守備、魔防+6、「敵の強化の+を無効」を付与(1ターン)<br>敵から攻撃された時、または、戦闘開始時、敵のHPが75%以上の時、戦闘中、敵の攻撃、守備、魔防が、戦闘開始時の自分の守備の15%+5だけ減少、自分が受けるダメージ-自分の守備の15%(範囲奥義を除く)、かつ戦闘中、自分のHPが2以上で敵の致死攻撃を受けた時、ダメージをHPが1残るように軽減(1戦闘1回のみ、奥義以外の「敵の致死攻撃を受けた時、ダメージをHPが1残るように軽減する効果」とは重複しない)<br>敵から攻撃された時、または、戦闘開始時、敵のHPが75%以上の時、戦闘後、7回復',NULL,'',NULL,'','','',NULL,'武器','',NULL,'斧','不可','','',NULL,16,NULL,NULL,NULL,400,'|飛行|',NULL,'',NULL,NULL,NULL,3,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
