INSERT INTO skills VALUES(3023,'聖騎士の槍',NULL,NULL,'','','【再移動(3)】を発動可能<br>奥義が発動しやすい(発動カウント-1)<br>ターン開始時、自分に【迅走】を付与<br>自分から攻撃した時、または、周囲2マス以内に味方がいる時、戦闘中、攻撃、速さ、守備、魔防が自身を中心とした縦3列と横3列にいる敵の数×3+5だけ増加(最大14)、ダメージ+守備の20%(戦闘前奥義も含む)、敵の奥義以外のスキルによる「ダメージを○○％軽減」を半分無効(無効にする数値は端数切捨て)(範囲奥義を除く)、戦闘後、7回復','','',NULL,'','','','','','',NULL,NULL,NULL,'','',NULL,NULL,NULL,NULL,NULL,NULL,'','','',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
