PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS 'skills' (
    'id' INTEGER PRIMARY KEY DEFAULT '',
    'name' ,
    'release_date' DATETIME,
    'refined_date' DATETIME,
    'how_to_read' TEXT DEFAULT '',
    'english_name'  DEFAULT '',
    'description' ,
    'must_learn'  DEFAULT '',
    'refine_from' TEXT DEFAULT '',
    'can_status_refine' BOOLEAN,
    'refine_description'  DEFAULT '',
    'refine_description2' TEXT DEFAULT '',
    'special_refine_description' TEXT DEFAULT '',
    'special_refine_hp' TEXT DEFAULT '',
    'type'  DEFAULT '',
    'assist_type' TEXT DEFAULT '',
    'sacred_seal' BOOLEAN,
    'weapon_type' ,
    'inherit' ,
    'inheritable_weapon_type' TEXT DEFAULT '',
    'inheritable_move_type'  DEFAULT '',
    'count' INTEGER,
    'might' INTEGER,
    'might_refine' INTEGER,
    'no_impl_required' BOOLEAN,
    'range' INTEGER,
    'sp' INTEGER,
    'effective' TEXT DEFAULT '',
    'invalidate_effective' TEXT DEFAULT '',
    'consideration' TEXT DEFAULT '',
    'hp' INTEGER,
    'atk' INTEGER,
    'spd' INTEGER,
    'def' INTEGER,
    'res' INTEGER,
    'cooldown_count' INTEGER,
    'atk_count' INTEGER,
    'counteratk_count' INTEGER,
    'all_dist_counter' BOOLEAN,
    'disable_counter' BOOLEAN,
    'wrathful_staff' BOOLEAN
);
COMMIT;
